import React, { useState } from "react";
import { motion } from "framer-motion";

const Example = () => {
    const [isOpen, setIsOpen] = useState(false)
    const [isToggle, setIsToggle] = useState(false);
    if(isToggle) document.querySelector("body").style.backgroundColor = "rgba(0,0,0, .8)";
    else document.querySelector("body").style.backgroundColor = "white"
    return (
    <div>
    <motion.div onClick={() => setIsOpen(!isOpen) }className="Test1" animate={{ rotate: isOpen ?  180 : 0, backgroundColor: isOpen? "red" : "violet" }} transition= {{ duration: .4 }} >
    </motion.div>
    <motion.div style={{ position: "absolute", width: "200px", height: "200px", border: "solid 3px red"}}
    drag
    dragConstraints={{ left: 0, right: 500, top: 0, bottom: 500 }}
    dragMomentum={false}
    // dragTransition={{ bounceStiffness: 600, bounceDamping: 10 }}
    onDragStart={ () => {
        document.querySelector("body").style.backgroundColor = "lightgrey";
    }}
    onDragEnd={ () => {
        document.querySelector("body").style.backgroundColor = "white";
    }}
    ></motion.div>
    <motion.div
    style={{width: "150px", height: "150px", borderRadius: "30px", backgroundColor: "blue"}}
    animate={{scale: [0, 1, 2, 1] }}
    ></motion.div>



    <div className="ToggleWrapper">
        <motion.div className="Toggle"
        onClick={() => setIsToggle(!isToggle)}
        animate={{x: isToggle ? "95%" : 0}}
        drag="x"
        dragConstraints={{left: 0, right: "95%"}}
        dragMomentum={false}
        onDrag={(event, info) => { setIsToggle(info.point.x >= 40? true : false)}}
        ></motion.div>
    </div>

    </div>)
}

export default Example;
