import React, { useState } from "react";
import { motion } from "framer-motion";

const Example = () => {
    const [isOpen, setIsOpen] = useState(false)
    return <motion.div onClick={() => setIsOpen(!isOpen) }className="Test1" animate={{ rotate: isOpen ?  180 : 0, backgroundColor: isOpen? "red" : "violet" }} transition= {{ duration: .4 }} ></motion.div>
}

export default Example;
