import React, { useState } from "react";
import { motion } from "framer-motion";

const Example = () => {
    const [isOpen, setIsOpen] = useState(false)
    return (
    <div>
    <motion.div onClick={() => setIsOpen(!isOpen) }className="Test1" animate={{ rotate: isOpen ?  180 : 0, backgroundColor: isOpen? "red" : "violet" }} transition= {{ duration: .4 }} >
    <motion.div style={{ width: "200px", height: "200px", border: "solid 3px red"}}drag></motion.div>
    </motion.div>
    </div>)
}

export default Example;
